var app = angular.module('angularapp', ['ngSanitize']);
app.controller('AngularAppCtrl', function($scope, $sce) {
    $scope.temp = '';
    $scope.btn = '';
    $scope.snippet = '';
    $scope.username = 'wickY26';


    $scope.unsafeString = '<h1>Hello this </h1>';
     $scope.obj = '<h1>Hello this </h1>';


    $scope.myDirective = '<test/>';

    $scope.btn = function() {




        var ga = new GlideAjax('getData');
        ga.addParam('sysparm_name', 'getUserData');
        ga.getXML(HelloWorldParse);

        function HelloWorldParse(response) {
            var answer = response.responseXML.documentElement.getAttribute("answer");
            $scope.unsafeString = JSON.parse(answer).script;
			//$scope.obj= JSON.parse(answer).ojbsc;
			//alert($scope.unsafeString);
        }

    };

});


app.directive('bindUnsafeHtml', ['$compile', function($compile) {
    return function(scope, element, attrs) {
        scope.$watch(
            function(scope) {
                // watch the 'bindUnsafeHtml' expression for changes
                return scope.$eval(attrs.bindUnsafeHtml);
            },
            function(value) {
                // when the 'bindUnsafeHtml' expression changes
                // assign it into the current DOM
                element.html(value);

                // compile the new DOM and link it to the current
                // scope.
                // NOTE: we only compile .childNodes so that
                // we don't get into infinite loop compiling ourselves
                $compile(element.contents())(scope);
            }
        );
    };
}]);
